/* @jsx mdx */

export const toc = [{
  value: 'Documentation',
  id: 'documentation',
  children: []
}, {
  value: 'Token-based authentication',
  id: 'token-based-authentication',
  children: []
}, {
  value: 'Browsing GraphQL in development',
  id: 'browsing-graphql-in-development',
  children: []
}, {
  value: 'Updating GraphQL schema',
  id: 'updating-graphql-schema',
  children: []
}];

const layoutProps = {
  toc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <p>{`Pupilfirst LMS uses a GraphQL API to enable typed communication between the ReScript front-end code and the Rails
server. A few of these GraphQL queries and mutations can be accessed using token-based authentication.`}</p>
    <h2 {...{
      "id": "documentation"
    }}>{`Documentation`}</h2>
    <p>{`Documentation for queries and mutations that are accessible via token-based authentication can be found here:
`}<a parentName="p" {...{
        "href": "https://pupilfirst.github.io/pupilfirst-api-docs"
      }}>{`https://pupilfirst.github.io/pupilfirst-api-docs`}</a></p>
    <h2 {...{
      "id": "token-based-authentication"
    }}>{`Token-based authentication`}</h2>
    <p>{`To generate a token on a self-hosted instance, simply call the `}<inlineCode parentName="p">{`user.regenerate_api_token`}</inlineCode>{` method, and read the token
by calling `}<inlineCode parentName="p">{`user.api_token`}</inlineCode>{`. Make sure to save the token in your application's configuration; it cannot be accessed
again from the database.`}</p>
    <p>{`To authenticate using the token, pass the token in the `}<inlineCode parentName="p">{`Authorization`}</inlineCode>{` header:`}</p>
    <pre><code parentName="pre" {...{}}>{`Authorization: Bearer ACCESS_TOKEN
`}</code></pre>
    <h2 {...{
      "id": "browsing-graphql-in-development"
    }}>{`Browsing GraphQL in development`}</h2>
    <p>{`Visit the `}<inlineCode parentName="p">{`/graphiql`}</inlineCode>{` path to browse and interact with all GraphQL queries in the development environment.`}</p>
    <h2 {...{
      "id": "updating-graphql-schema"
    }}>{`Updating GraphQL schema`}</h2>
    <p>{`If you make any changes to the GraphQL schema, you'll need to update the `}<inlineCode parentName="p">{`graphql_schema.json`}</inlineCode>{` file by running an
introspection query.`}</p>
    <p>{`Run the following rake task to generate the schema:`}</p>
    <pre><code parentName="pre" {...{}}>{`rails graphql:schema:json
`}</code></pre>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
