/* @jsx mdx */

export const toc = [{
  value: 'Organization of en.yml',
  id: 'organization-of-enyml',
  children: []
}, {
  value: 'Visual Studio Code plugin',
  id: 'visual-studio-code-plugin',
  children: [{
    value: 'Usage',
    id: 'usage',
    children: []
  }]
}, {
  value: 'Third-party library translations',
  id: 'third-party-library-translations',
  children: []
}];

const layoutProps = {
  toc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <p>{`Pupilfirst LMS uses `}<em parentName="p">{`Rails Internationalization`}</em>{` (I18n) API for internationalization and localization.`}</p>
    <h2 {...{
      "id": "organization-of-enyml"
    }}>{`Organization of en.yml`}</h2>
    <table>
      <thead parentName="table">
        <tr parentName="thead">
          <th parentName="tr" {...{
            "align": null
          }}>{`Key`}</th>
          <th parentName="tr" {...{
            "align": "center"
          }}>{`Value`}</th>
        </tr>
      </thead>
      <tbody parentName="table">
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}><inlineCode parentName="td">{`components.COMPONENT.*`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "center"
          }}>{`Component-specific strings.`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}><inlineCode parentName="td">{`jobs.[MODULE].JOB.*`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "center"
          }}>{`Strings used by jobs.`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}><inlineCode parentName="td">{`models.MODEL.COLUMN.*`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "center"
          }}>{`Translation of database values to display values.`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}><inlineCode parentName="td">{`mutations.MUTATION.*`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "center"
          }}>{`Strings used by mutations.`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}><inlineCode parentName="td">{`queries.QUERY.*`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "center"
          }}>{`Strings used by resolvers and mutators.`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}><inlineCode parentName="td">{`shared.*`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "center"
          }}>{`Shared strings.`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}><inlineCode parentName="td">{`CONTROLLER.ACTION.*`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "center"
          }}>{`Strings used in traditional views.`}</td>
        </tr>
      </tbody>
    </table>
    <ul>
      <li parentName="ul"><inlineCode parentName="li">{`CONTROLLER`}</inlineCode>{` is always the plural version. For example: `}<inlineCode parentName="li">{`FoundersController#edit`}</inlineCode>{` is keyed as `}<inlineCode parentName="li">{`founders.edit.*`}</inlineCode></li>
      <li parentName="ul">{`Always order keys alphabetically. If you use `}<a parentName="li" {...{
          "href": "#visual-studio-code-plugin"
        }}>{`the VSCode plugin`}</a>{`, this will be done automatically.`}</li>
      <li parentName="ul">{`Third-party library translations follow their own format, and any customization of those should be `}<a parentName="li" {...{
          "href": "#third-party-library-translations"
        }}>{`documented
`}<em parentName="a">{`here`}</em>{` in this file`}</a>{`.`}</li>
    </ul>
    <h2 {...{
      "id": "visual-studio-code-plugin"
    }}>{`Visual Studio Code plugin`}</h2>
    <p><strong parentName="p">{`Important Note:`}</strong>{` This plugin only works within ReScript (`}<inlineCode parentName="p">{`.res`}</inlineCode>{`) files at the moment.`}</p>
    <p>{`You can use the `}<a parentName="p" {...{
        "href": "https://marketplace.visualstudio.com/items?itemName=bodhi.pupilfirst-translator"
      }}>{`pupilfirst-translator VSCode plugin`}</a>{`
to quickly move strings from UI components to the I18n YAML file.`}</p>
    <h3 {...{
      "id": "usage"
    }}>{`Usage`}</h3>
    <ol>
      <li parentName="ol">{`Select a string that needs to be moved to `}<inlineCode parentName="li">{`en.yml`}</inlineCode>{`.`}</li>
      <li parentName="ol">{`Right click and select `}<em parentName="li">{`Pupilfirst Translate`}</em>{` button. You can also use the `}<inlineCode parentName="li">{`Ctrl+Shift+i`}</inlineCode>{` keyboard shortcut.`}</li>
      <li parentName="ol">{`Enter the key. For example: `}<inlineCode parentName="li">{`video.description_label`}</inlineCode>{`.`}</li>
      <li parentName="ol">{`Press `}<inlineCode parentName="li">{`Enter`}</inlineCode>{` to confirm.`}</li>
    </ol>
    <p>{`This will create an entry for the `}<inlineCode parentName="p">{`key`}</inlineCode>{` you have added in `}<inlineCode parentName="p">{`en.yml`}</inlineCode>{` file and replaces the selection.`}</p>
    <h2 {...{
      "id": "third-party-library-translations"
    }}>{`Third-party library translations`}</h2>
    <ul>
      <li parentName="ul"><inlineCode parentName="li">{`errors.messages.content_type_invalid`}</inlineCode>{` and `}<inlineCode parentName="li">{`errors.messages.limit_out_of_range`}</inlineCode>{` are custom error messages for
the `}<a parentName="li" {...{
          "href": "https://github.com/igorkasyanchuk/active_storage_validations#internationalization-i18n"
        }}>{`active_storage_validations`}</a>{` gem.`}</li>
    </ul>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
